import axios from 'axios';
import React, { useState } from 'react';

function App() {
  const [fortune, setFortune] = useState('');
  const [color, setColor] = useState('black');

  const generateFortune = () => {
    axios
      .post('https://api.openai.com/v1/engines/text-davinci-003/jobs', {
        prompt: 'Generate a fortune cookie sentence.',
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer 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`,
          'x-openai-organization': `org-JjVrQMhF3l4VrbGNU1CEoo4t`
        },
      })
      .then((response) => {
        setFortune(response.data.choices[0].text);
        setColor(getRandomColor());
      });
  };

  const getRandomColor = () => {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  return (
    <div>
      <button onClick={generateFortune}>Generate Fortune</button>
      <p style={{ color }}>{fortune}</p>
    </div>
  );
}

export default App;
