    setSelectedType(type);
    setFortuneType(type);
  };

  return (
    <div className='cards'>
      <h2>Select a Fortune Type</h2>
      <span onClick={() => {setTimeout(() => {
  handleSelection('love');
}, 2000);}}> 
        <Card front={""} backText={"Love"}/>
