import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [fortune, setFortune] = useState('');

  const generateFortune = () => {
    axios
      .post('https://api.openai.com/v1/engines/text-davinci/jobs', {
        prompt: 'Generate a fortune cookie sentence.',
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer KMdB78Pxp5OTjbWWJWK9T3BlbkFJQth7EKxIR33X1xPBcbvD',
        },
      })
      .then((response) => {
        setFortune(response.data.choices[0].text);
      });
  };

  return (
    <div>
      <button onClick={generateFortune}>Generate Fortune</button>
      <p>{fortune}</p>
    </div>
  );
}

export default App;
