import React from "react";

const FortuneStyle = ({ setType, type }) => {
  return (
    <div>
      <h2>Choose a type of fortune</h2>
      <div>
        <button
          onClick={() => setType("riddle")}
          style={{ backgroundColor: type === "riddle" ? "blue" : "white" }}
        >
          Riddle
        </button>
        <button
          onClick={() => setType("direct")}
          style={{ backgroundColor: type === "direct" ? "blue" : "white" }}
        >
          Direct
        </button>
        <button
          onClick={() => setType("mystical")}
          style={{ backgroundColor: type === "mystical" ? "blue" : "white" }}
        >
          Mystical
        </button>
        <button
          onClick={() => setType("modern")}
          style={{ backgroundColor: type === "modern" ? "blue" : "white" }}
        >
          Modern
        </button>
      </div>
    </div>
  );
};

export default FortuneStyle;
