import React, { useState } from "react";
import Card from './Card';

const AuraColor = ({ setAuraColor }) => {
  const [selectedColor, setSelectedColor] = useState(null);

  const handleColorSelection = (color) => {
    setSelectedColor(color);
    setAuraColor(color);
  };

  return (
    <div className="cards color-cards">
      <h2>Select Aura Color</h2>
      <div>   
        <span onClick={() => handleColorSelection("red")}>
        <Card front={""} backText={"Red"}/>
        </span>
        <span onClick={() => handleColorSelection("Blue")}>
          <Card front={""} backText={"Orange"}/>
        </span>
        <span onClick={() => handleColorSelection("yellow")}>
          <Card front={""} backText={"Yellow"}/>
        </span>
        <span onClick={() => handleColorSelection("green")}>
          <Card front={""} backText={"Green"}/>
        </span>
        </div>
    </div>
  );
};

export default AuraColor;
